mapper.select:
SELECT count(*) FROM offerer
WHERE
	1=1

	AND id = #{id}


	AND name like CONCAT('%', #{name}, '%')


	AND offererName like CONCAT('%', #{offererName}, '%')


	AND registerDate = #{registerDate}


	AND offererNumber = #{offererNumber}


	AND phone like CONCAT('%', #{phone}, '%')


	AND cellPhone like CONCAT('%', #{cellPhone}, '%')


	AND sidoId = #{sidoId}


	AND sigunguId = #{sigunguId}


	AND address1 like CONCAT('%', #{address1}, '%')


	AND address2 like CONCAT('%', #{address2}, '%')

SELECT id, name FROM offerer
WHERE
	1=1

	AND id = #{id}


	AND name like CONCAT('%', #{name}, '%')


	AND offererName like CONCAT('%', #{offererName}, '%')


	AND registerDate = #{registerDate}


	AND offererNumber = #{offererNumber}


	AND phone like CONCAT('%', #{phone}, '%')


	AND cellPhone like CONCAT('%', #{cellPhone}, '%')


	AND sidoId = #{sidoId}


	AND sigunguId = #{sigunguId}


	AND address1 like CONCAT('%', #{address1}, '%')


	AND address2 like CONCAT('%', #{address2}, '%')

SELECT a.id id, a.name name, a.password password, a.offererName offererName, a.registerDate registerDate, a.offererNumber offererNumber, a.offererBrief offererBrief, a.phone phone, a.cellPhone cellPhone, a.payDate payDate, a.eosDate eosDate, a.mapFilename mapFilename FROM ( SELECT id FROM offerer WHERE 1=1
AND id = #{id}
AND name like CONCAT('%', #{name}, '%')
AND offererName like CONCAT('%', #{offererName}, '%')
AND registerDate = #{registerDate}
AND offererNumber = #{offererNumber}
AND phone like CONCAT('%', #{phone}, '%')
AND cellPhone like CONCAT('%', #{cellPhone}, '%')
AND sidoId = #{sidoId}
AND sigunguId = #{sigunguId}
AND address1 like CONCAT('%', #{address1}, '%')
AND address2 like CONCAT('%', #{address2}, '%')
LIMIT ${start}, ${end}
) b JOIN offerer a ON a.id = b.id
