mapper.select:
SELECT count(*) FROM requirement
WHERE
	1=1

	AND id = #{id}


	AND offererId = #{offererId}


	AND workDate = #{workDate}


	AND workAbility = #{workAbility}


	AND matchStatus = #{matchStatus}


	AND registerDate = #{registerDate}


	AND gender = #{gender}

SELECT 
	a.id id,
	a.offererId offererId,
	a.workDate workDate,
	a.workTime workTime,
	a.ageRange ageRange,
	a.workAbility workAbility,
	a.nation nation,
	a.registerDate registerDate,
	a.matchStatus matchStatus,
	a.gender gender,
	(
		SELECT b.id FROM offerer a, juso b 
		WHERE a.id = offererId 
		AND a.sidoId = b.sidoId 
		AND a.sigunguId = b.sigunguId
	) location,
	(
		SELECT offererName FROM offerer WHERE id = a.offererId
	) offererName
FROM (
	SELECT
		id
	FROM
		requirement
	WHERE 1=1

		AND id = #{id}


		AND offererId = #{offererId}


		AND workDate = #{workDate}


		AND workAbility = #{workAbility}


		AND matchStatus = #{matchStatus}


		AND registerDate = #{registerDate}


		AND gender = #{gender}


		LIMIT ${start}, ${end}

) b
JOIN requirement a ON a.id = b.id

SELECT id, offererId, workDate, workTime, ageRange, workAbility, nation, registerDate, matchStatus, gender, ( SELECT b.id FROM offerer a, juso b WHERE a.id = offererId AND a.sidoId = b.sidoId AND a.sigunguId = b.sigunguId ) location, ( SELECT offererName FROM offerer WHERE id = a.offererId ) offererName FROM requirement WHERE 1=1
AND id = #{id}
AND offererId = #{offererId}
