mapper.insert:
INSERT INTO seeker ( id, name, birth, gender, nation, region1, region2, region3, workMday, workQtime, licenseFile, workAbility, payDate, eosDate ) VALUES ( #{id}, #{name}, #{birth, typeHandler=org.apache.ibatis.type.DateTypeHandler}, #{gender}, #{nation},
#{item.id}
#{workMday.value}, #{workQtime.value}, #{licenseFile}, #{workAbility}, #{payDate}, #{eosDate} )

mapper.select:
SELECT count(*) FROM seeker
WHERE
	1=1

	AND id = #{id}


	AND name = #{name}


	AND gender = #{gender}


	AND nation = #{nation}


	AND (region1 = #{region} OR region2 = #{region} OR region3 = #{region})


	AND workMday = #{workMday}


	AND workQtime = #{workQtime}


	AND workAbility = #{workAbility}

SELECT a.id id, a.name name, a.birth birth, a.gender gender, a.registerDate registerDate, a.nation nation, a.region1 region1, a.region2 region2, a.region3 region3, a.workMday workMday, a.workQtime workQtime, a.licenseFile licenseFile, a.workAbility workAbility, a.payDate payDate, a.eosDate eosDate FROM ( SELECT id FROM seeker WHERE 1=1
AND id = #{id}
AND name like CONCAT('%', #{name}, '%')
AND gender = #{gender}
AND nation = #{nation}
AND (region1 = #{region} OR region2 = #{region} OR region3 = #{region})
AND workMday = #{workMday}
AND workQtime = #{workQtime}
AND workAbility = #{workAbility}
LIMIT ${start}, ${end}
) b JOIN seeker a ON a.id = b.id
